import { ADDTOCART, EMAIL, SECURE } from "../constants";
import { widthValue } from "../helpers"

describe("widthValue", () => {
  it("should return the width value if it is provided", () => {
    const props = { width: "50%", context: "Primary" };
    expect(widthValue(props)).toEqual("50%");
  });

  it('should return "100%;" for ADDTOCART, EMAIL, and SECURE contexts', () => {
    expect(widthValue({ context: ADDTOCART })).toEqual("100%;");
    expect(widthValue({ context: EMAIL })).toEqual("100%;");
    expect(widthValue({ context: SECURE })).toEqual("100%;");
  });

  it('should return "auto" for all other contexts', () => {
    const props = { context: "Primary" };
    expect(widthValue(props)).toEqual("auto");
  });
});
