import React from "react";
import styled from "styled-components";
import {
  baseFlexBox,
  baseMarginStyles,
  desktopFlexBox,
  desktopMarginStyles,
  tabletFlexBox,
  tabletMarginStyles,
  baseSizeStyles,
  tabletSizeStyles,
  desktopSizeStyles,
} from "../styles/styles";

import { ButtonList } from "./ButtonList";

const Container = styled.div`
  ${baseFlexBox}
  ${baseMarginStyles}
  ${baseSizeStyles}

  @media (min-width: 768px) {
    ${tabletMarginStyles}
    ${tabletFlexBox}
    ${tabletSizeStyles}
  }
  @media (min-width: 1440px) {
    ${desktopFlexBox}
    ${desktopMarginStyles}
    ${desktopSizeStyles}
  }
  gap: 0.5rem;
  z-index: 10;
`;
export const ButtonGroup = ({ buttons = [], tabletStyles = {}, buttonContainerStyles = {}, desktopStyles = {} }) => {
  const flexDirection = buttonContainerStyles?.flexDirection ?? "row";

  return (
    <Container
      {...buttonContainerStyles}
      flexDirection={flexDirection}
      tabletStyles={tabletStyles}
      desktopStyles={desktopStyles}
    >
      <ButtonList buttons={buttons} />
    </Container>
  );
};
