import React from "react";
import { ButtonVideo } from "./ButtonVideo";
import { CMSButton } from "./CMSButton";

export const ButtonList = ({ buttons }) => {
  return (
    <>
      {buttons.map(({ type, label, onClick = () => null, onContextMenu = () => null, responsiveStyles, adornment = "", ...rest }) => {
        if (type === "buttonYoutube") {
          const { youTubeID } = rest;
          return (
            <ButtonVideo
              adornment={adornment}
              backgroundColor={rest?.backgroundColor}
              color={rest?.color}
              desktopStyles={responsiveStyles?.desktop}
              href={rest?.url}
              key={youTubeID + label}
              label={label}
              onClick={onClick}
              onContextMenu={onContextMenu}
              tabletStyles={responsiveStyles?.tablet}
              width={rest?.width}
              youTubeID={youTubeID}
            />
          );
        }
        const { url } = rest;
        return (
          <CMSButton
            adornment={adornment}
            backgroundColor={rest?.backgroundColor}
            color={rest?.color}
            desktopStyles={responsiveStyles?.desktop}
            href={url}
            key={url + label}
            label={label}
            onClick={onClick}
            onContextMenu={onContextMenu}
            tabletStyles={responsiveStyles?.tablet}
            width={rest?.width}
          />
        );
      })}
    </>
  );
};
