import React from "react";
import styled from "styled-components";
import Imgix from "react-imgix";

const StyledImage = styled(Imgix)`
  object-fit: cover;
  object-position: ${({ objectPosition = "50% 50%" }) => objectPosition};
  width: ${({ width }) => `${width}px`};
  height: ${({ height }) => `${height}px`};
  @media (min-width: 768px) {
    ${(props) => props.tabletStyles?.objectPosition && `object-position: ${props.tabletStyles.objectPosition};`};
  }
`;
const Image = ({ alt, height, src, width, objectPosition, responsiveStyles }) => {
  return (
    <StyledImage
      objectPosition={objectPosition}
      src={src}
      alt={alt}
      height={height}
      width={width}
      tabletStyles={responsiveStyles.tablet}
    />
  );
};
export default Image;
