import React from "react";
import { Modal } from "@arcteryx/components-modal";

export const YoutubeVideoModal = ({ youTubeID, isModalVisible, setIsModalVisible, modalCaller }) => {
  return (
    <Modal
      onVideoPlaybackEnded={() => null}
      modalIsShown={isModalVisible}
      toggleModal={setIsModalVisible}
      modalCaller={modalCaller}
      video
    >
      <div id="yt-player" data-video-url={`//youtube.com/embed/${youTubeID}?autoplay=1`}></div>
    </Modal>
  );
};
