import React from "react";
import styled from "styled-components";
import { H1 } from "@arcteryx/components-typography";
import {
  baseMarginStyles,
  baseFontStyles,
  tabletFontStyles,
  tabletMarginStyles,
  desktopFontStyles,
  desktopMarginStyles,
} from "../styles/styles";
const StyledH1 = styled(H1)`
  ${baseFontStyles}
  ${baseMarginStyles}

  @media (min-width: 768px) {
    ${tabletFontStyles}
    ${tabletMarginStyles}
  }

  @media (min-width: 1440px) {
    ${desktopFontStyles}
    ${desktopMarginStyles}
  }
`;

export const Headline = ({
  color,
  content,
  desktopStyles = {},
  fontFamily,
  fontSize,
  fontWeight,
  letterSpacing,
  lineHeight,
  marginBottom,
  marginLeft,
  marginRight,
  marginTop,
  tabletStyles = {},
  textAlign,
  textShadow,
  textTransform,
}) => (
  <StyledH1
    color={color}
    tabletStyles={tabletStyles}
    desktopStyles={desktopStyles}
    fontFamily={fontFamily}
    fontSize={fontSize}
    fontWeight={fontWeight}
    letterSpacing={letterSpacing}
    lineHeight={lineHeight}
    textAlign={textAlign}
    textShadow={textShadow}
    textTransform={textTransform}
    marginTop={marginTop}
    marginRight={marginRight}
    marginLeft={marginLeft}
    marginBottom={marginBottom}
    data-testid="headline"
    dangerouslySetInnerHTML={{ __html: content }}
  />
);
