import React from "react";
import styled from "styled-components";
import {
  baseMarginStyles,
  baseFontStyles,
  tabletFontStyles,
  tabletMarginStyles,
  desktopFontStyles,
  desktopMarginStyles,
} from "../styles/styles";

const StyledText = styled.p`
  ${baseFontStyles}
  ${baseMarginStyles}

  @media (min-width: 768px) {
    ${tabletFontStyles}
    ${tabletMarginStyles}
  }

  @media (min-width: 1440px) {
    ${desktopFontStyles}
    ${desktopMarginStyles}
  }
`;

export const TextArea = ({
  color,
  content,
  desktopStyles = {},
  fontFamily,
  fontSize,
  fontWeight,
  letterSpacing,
  lineHeight,
  marginBottom,
  marginLeft,
  marginRight,
  marginTop,
  tabletStyles = {},
  textAlign,
  textShadow,
  textTransform,
}) => {
  return (
    <StyledText
      desktopStyles={desktopStyles}
      tabletStyles={tabletStyles}
      color={color}
      fontFamily={fontFamily}
      fontSize={fontSize}
      fontWeight={fontWeight}
      letterSpacing={letterSpacing}
      lineHeight={lineHeight}
      textAlign={textAlign}
      textShadow={textShadow}
      textTransform={textTransform}
      marginTop={marginTop}
      marginRight={marginRight}
      marginLeft={marginLeft}
      marginBottom={marginBottom}
      data-testid="textarea"
      dangerouslySetInnerHTML={{ __html: content }}
    />
  );
};
