/* istanbul ignore file */
import React from "react";
import { ErrorText } from "./Styles";
import Tooltip from "./Tooltip/Tooltip";
import Label from "./Label";

const LabelHeaderField = ({ id, label, required, toolTip, arrowDirection, isInfo, error, attachColon = true }) => (
  <div>
    {label && <Label htmlFor={id} name={id} labelText={label} attachColon={attachColon} required={required} />}
    {toolTip && <Tooltip title={toolTip} arrowDirection={arrowDirection} isInfo={isInfo} />}
    {error && (
      <ErrorText role="alert" className="qa-form-error-text">
        {error.message}
      </ErrorText>
    )}
  </div>
);

export default LabelHeaderField;
