import React from "react";
import styled from "styled-components";

export const LabelStyled = styled.label`
  font-size: 14px;
  line-height: 1;
  margin-bottom: 0.5em;
  display: inline-block;
`;

export const RequiredSpan = styled.span`
  color: var(--warning-red);
  font-size: 14px;
  line-height: 1;
  margin: 0;
`;

const Label = ({ name, required, labelText, attachColon = true }) => (
  <LabelStyled className="qa-control-label" id={`labelFor--${name}`} htmlFor={name}>
    {required && <RequiredSpan>*</RequiredSpan>}
    {labelText}
    {attachColon && ":"}
  </LabelStyled>
);

export default Label;
