const Front = styled.div`
  width: ${props => props.width || "100%"};
  height: 100%;
  display: block;
  padding: 10px 0;
  border: 1px solid var(--colour-grey-pinline);
  margin: 0 auto;
  text-align: center;
`;

const StyledUL = styled.ul`
  display: grid;
  grid-template-columns: repeat(4, 25%);
  grid-gap: 1rem;
  margin: 0 auto !important;

  li {
    width: 100%;
    display: flex;
    border: 1px solid var(--colour-grey-pinline);
    height: inherit;
    text-align: center;
    background-color: var(--colour-grey-background); /* Just for visualization */
    padding: 1rem;
    margin: 0 auto;
    justify-content: center;
    align-items: center;
    flex-direction: column;
  }
`;
const StyledDivPayment = styled.div`
  display: flex;
  flex-wrap: wrap;
  width: 15rem;
  justify-content: left;
  grid-gap: 0.5rem;
  > svg {
    height: "auto";
  }
  @media only screen and (max-width: ${ScreenSize.md}px) {
    justify-content: center;
  }
`;

const IconItem = ({ type, width, height, title, color, className }) => (
  <li className={className}>
    {title}
    <br /> <Icon type={type} width={width} height={height} title={title} color={color} />
  </li>
);

