import React from "react";
import RenderContent from "./Content";
import { ContentWrapper, InnerWrapper, LeftContent, RightContent } from "./Style";

const HelpBottomLayout = ({ hideTopBorder, isHtml, left, right, leftWidth, rightWidth, isHelpPageSizing }) => (
  <ContentWrapper hideTopBorder={hideTopBorder}>
    <InnerWrapper>
      {left && (
        <LeftContent className="help-bottom-layout--left" noTopPadding={hideTopBorder} leftWidth={leftWidth}>
          <RenderContent
            items={left}
            isHtml={isHtml}
            isHelpPageSizing={isHelpPageSizing}
            hideTopBorder={hideTopBorder}
            width={leftWidth}
          />
        </LeftContent>
      )}
      {right && (
        <RightContent
          className="help-bottom-layout--right"
          noTopPadding={hideTopBorder}
          rightWidth={rightWidth}
          isHelpPageSizing={isHelpPageSizing}
        >
          <RenderContent
            items={right}
            isHtml={isHtml}
            isHelpPageSizing={isHelpPageSizing}
            hideTopBorder={hideTopBorder}
            width={rightWidth}
          />
        </RightContent>
      )}
    </InnerWrapper>
  </ContentWrapper>
);

export default HelpBottomLayout;
