import React from "react";

export const mockTypography = {
  Breakpoint: jest.fn().mockImplementation((breakpoint: string) => {
    return (strings: TemplateStringsArray) => {
      return `@media (min-width: ${breakpoint}) { ${strings.join("")} }`;
    };
  }),
  P: ({ children }: { children: React.ReactNode }) => <p>{children}</p>,
  H1: ({ children }: { children: React.ReactNode }) => <h1>{children}</h1>,
  H2: ({ children }: { children: React.ReactNode }) => <h2>{children}</h2>,
  H3: ({ children }: { children: React.ReactNode }) => <h3>{children}</h3>,
  H4: ({ children }: { children: React.ReactNode }) => <h4>{children}</h4>,
};

export const mockIcons = {
  Icon: ({ type }: { type: string }) => <div>{type}</div>,
};

export const mockModal = {
  Modal: ({ type }: { type: string }) => <div></div>,
};

export const mockButton = {
  Button: ({ children, href }: { children: React.ReactNode; href: string }) => (
    <a href={href}>{children}</a>
  ),
};
