#!/bin/bash
# Note nyc uses the config in .nycrc.json

rm -rf all-coverage-reports
rm -rf .nyc_output
mkdir all-coverage-reports
cp coverage/coverage-final.json all-coverage-reports/coverage-unit-final.json
npx nyc merge all-coverage-reports .nyc_output/out.json
npx nyc report --report-dir merged-coverage-report