import styled from "styled-components";

const fontSize = (large, loud, louder) => {
  if (large) {
    return "2rem";
  }
  if (loud) {
    return "2.5rem";
  }
  if (louder) {
    return "3rem";
  }
  return "1.5rem";
};

export const H1 = styled.h1`
  margin-bottom: 1rem;
  font-family: ${props =>
    ["urban", "veilance"].includes(props.market.toLowerCase())
      ? '"Suisse Intl Regular", arial, sans-serif'
      : '"urw-din-condensed", arial, sans-serif'};
  font-size: ${props => fontSize(props.large, props.loud, props.louder)};
  font-weight: 700;
  line-height: ${props => fontSize(props.large, props.loud, props.louder)};
  color: ${props => props.textColor};
  text-transform: uppercase;
`;

H1.defaultProps = {
  large: false,
  loud: false,
  louder: false,
  textColor: "#000",
  market: "outdoor",
};
export default H1;
