import React from "react";
import { type Meta, type StoryObj } from "@storybook/react";
import { CheckboxVariant as Checkbox } from "@arcteryx/components-form";

const initialProps = {
  checked: false,
  disabled: false,
  label: "Checkbox",
  name: "Checkbox",
};

const meta: Meta<typeof Checkbox> = {
  title: "Design System / Atoms / Checkbox",
  component: Checkbox,
  args: initialProps,
};

export default meta;

type Story = StoryObj<any>;
type CheckboxArgs = Story["args"];

export const Default: Story = {
  render: (args: CheckboxArgs) => (
    <Checkbox {...args}> {(args as { label: string })?.label} </Checkbox>
  ),
  args: initialProps,
};
