import React from "react";
import { type ImageProps } from "./Image.types";

export const Image: React.FC<ImageProps> = ({
  alt = "",
  className,
  height,
  src,
  srcSet,
  sizes = "100vw",
  shouldLazyLoad = false,
  width,
}) => {
  return (
    <img
      alt={alt}
      className={className}
      height={height}
      sizes={sizes}
      src={src}
      srcSet={srcSet}
      loading={shouldLazyLoad ? "lazy" : "eager"}
      width={width}
    />
  );
};
