import React from "react";
import { render } from "@testing-library/react";
import { VideoPlayer } from "./VideoPlayer";
import "@testing-library/jest-dom";

describe("Video", () => {
  it("renders video Player element with correct props", () => {
    const props = {
      videoSrc: "https://example.com/video.mp4",
      autoPlay: true,
      loop: false,
      muted: true,
      playsInline: false,
    };
    const { container } = render(<VideoPlayer {...props} />);
    const videoElement = container.querySelector("video");
    // Check that the video element has the correct attributes
    expect(videoElement).toBeInTheDocument();

    // You can also test the source element if needed
    const sourceElement = container.querySelector("source");
    expect(sourceElement).toBeInTheDocument();
    expect(sourceElement).toHaveAttribute("src", props.videoSrc);
    expect(sourceElement).toHaveAttribute("type", "video/mp4");
  });
});
