import {
  ButtonModal,
  type ButtonModalProps,
} from "../../../organisms/buttonModal/ButtonModal";
import { Button } from "@arcteryx/components-button";

import React from "react";

interface Props extends ButtonModalProps {
  shouldOpenModal: boolean;
}

export const CardButton: React.FC<Props> = ({
  shouldOpenModal,
  label,
  onClick,
  onContextMenu,
  url,
}) => {
  return shouldOpenModal ? (
    <ButtonModal
      label={label}
      onClick={onClick}
      onContextMenu={onContextMenu}
      url={url ?? ""}
    />
  ) : (
    <Button href={url} onClick={onClick} onContextMenu={onContextMenu}>
      {label}
    </Button>
  );
};
