import React from "react";
import { LinkBox } from "./LinkBox";
import { render, screen } from "@testing-library/react";
import "@testing-library/jest-dom";

describe("LinkBox", () => {
  it("renders the link box with the correct props", () => {
    const props = {
      className: "test-class",
      icon: "play-button",
      label: "Sizing & Fit Guide",
      url: "//arcteryx.com/help/sizing?intcmp=sizing",
    };

    render(<LinkBox {...props} />);
    const link = screen.getByRole("link");
    expect(link).toHaveAttribute("href", props.url);
    expect(link).toHaveAttribute("aria-label", props.label);
    const svg = document.querySelector("svg");
    expect(svg).toBeInTheDocument();
    expect(svg).toHaveAttribute("height", "100%");
    expect(svg).toHaveAttribute("viewBox", "0 0 36 36");
    expect(svg).toHaveAttribute("width", "30");
    const text = screen.getByText(props.label);
    expect(text).toHaveTextContent(props.label);
  });
});
