import React from "react";
import { render } from "@testing-library/react";
import { RichTextIcon } from "./RichTextIcon";
import { richTextBlockList } from "../richText/__mocks__/blocks";

import "@testing-library/jest-dom";

describe("RichTextIcon", () => {
  it("renders the icon and rich text", () => {
    const { getByTitle, getByText } = render(
      <RichTextIcon content={richTextBlockList} icon={"amex-logo"} />,
    );
    const iconElement = getByTitle("Rich-Text-Icon");
    const richTextElement = getByText("Hello, world!");
    expect(iconElement).toBeInTheDocument();
    expect(richTextElement).toBeInTheDocument();
  });
});
