import "@testing-library/jest-dom";
import React from "react";
import { HeroBanner } from "./HeroBanner";
import { type HeroBannerProps } from "./HeroBanner.types";
import { render, screen } from "@testing-library/react";

const heroBannerProps: HeroBannerProps = {
  image: {
    src: "https://example.com/image.jpg",
    alt: "Example Image",
  },
  headline: "Test Headline",
  textArea: "Test Text Area",
  miceType: "Test Mice Type",
};

describe("HeroBanner", () => {
  it("renders the image correctly", () => {
    render(<HeroBanner {...heroBannerProps} />);
    const heroBackground = screen.getByTestId("hero-background");
    expect(heroBackground).toHaveStyle(
      `background-image: url(${heroBannerProps.image.src})`,
    );
  });

  it("renders the headline correctly", () => {
    render(<HeroBanner {...heroBannerProps} />);
    const contentElement = screen.getByText("Test Headline");
    expect(contentElement).toBeInTheDocument();
  });

  it("renders the text area correctly", () => {
    render(<HeroBanner {...heroBannerProps} />);
    const contentElement = screen.getByText("Test Text Area");
    expect(contentElement).toBeInTheDocument();
  });

  it("renders the mice type correctly", () => {
    render(<HeroBanner {...heroBannerProps} />);
    const contentElement = screen.getByText("Test Mice Type");
    expect(contentElement).toBeInTheDocument();
  });
});
