import "@testing-library/jest-dom";
import React from "react";
import { ShortHeroBanner } from "./ShortHeroBanner";
import { type ShortHeroBannerProps } from "./ShortHeroBanner.types";
import { render, screen } from "@testing-library/react";

const shortHeroBannerProps: ShortHeroBannerProps = {
  image: {
    src: "https://example.com/image.jpg",
    alt: "Example Image",
  },
};

describe("HeroBanner", () => {
  it("renders the image correctly", () => {
    render(<ShortHeroBanner {...shortHeroBannerProps} />);
    const heroBackground = screen.getByTestId("short-hero-background");
    expect(heroBackground).toHaveStyle(
      `background-image: url(${shortHeroBannerProps.image.src})`,
    );
  });
});
