import React from "react";
import styled from "styled-components";
import { ScreenSize } from "@arcteryx/components-typography";
import { type ShortHeroBannerProps } from "./ShortHeroBanner.types";

interface ShortHeroSectionProps {
  image: string;
}

export const ShortHeroSection = styled.section<ShortHeroSectionProps>`
  width: 100%;
  // Height is based on Viewport Minus Header
  // Content should fill the entire viewport
  height: 20vh;
  max-height: 150px;
  background-image: url(${({ image }) => image});
  background-size: cover;
  background-repeat: no-repeat;
  background-position: center;

  @media (min-width: ${ScreenSize.md}px) {
    /* styles for screens above 768 */
    height: 35vh;
    max-height: 260px;
  }
`;

export const ShortHeroBanner: React.FC<ShortHeroBannerProps> = ({ image }) => {
  return (
    <ShortHeroSection
      data-testid="short-hero-background"
      image={image.src}
    ></ShortHeroSection>
  );
};
